import time
from datetime import datetime

class Vehicle:
    def __init__(self, vehicle_id, initial_location):
        self.vehicle_id = vehicle_id
        self.location = initial_location

    def update_location(self):
        # Simulate GPS location update with random coordinates
        self.location = (self.location[0] + random.uniform(-0.01, 0.01), self.location[1] + random.uniform(-0.01, 0.01))

    def get_location(self):
        return self.location

class GPSMonitoringSystem:
    def __init__(self):
        self.vehicles = {}

    def add_vehicle(self, vehicle_id, initial_location):
        self.vehicles[vehicle_id] = Vehicle(vehicle_id, initial_location)
        print(f"Vehicle {vehicle_id} added.")

    def track_vehicles(self):
        while True:
            for vehicle_id, vehicle in self.vehicles.items():
                vehicle.update_location()
                print(f"Vehicle {vehicle_id} at {vehicle.get_location()} on {datetime.now()}")
            time.sleep(5)  # Update location every 5 seconds

def main():
    system = GPSMonitoringSystem()
    system.add_vehicle("V001", (40.7128, -74.0060))  # New York City coordinates
    system.add_vehicle("V002", (34.0522, -118.2437))  # Los Angeles coordinates
    system.track_vehicles()

if __name__ == "__main__":
    main()
